window.home={highlightMovies:[],carouselsMovies:[],init(){this.initialState()},initialState(){this.initDOMEvents(),this.getMovies()},initDOMEvents(){this.toggleNavActive(".nav-item")},toggleNavActive(t){$(t).click((function(){document.querySelectorAll(t).forEach(t=>{$(t).hasClass("active")&&t.classList.remove("active")}),$(this).addClass("active")}))},getMovies(){$.ajax({url:"https://sky-frontend.herokuapp.com/movies",type:"GET",success:t=>{this.setState(t)},error:t=>{console.log(t.statusText,t.status)}})},setState(t){this.highlightMovies=t.filter(t=>"highlights"===t.type),this.carouselsMovies=t.filter(t=>"carousel-portrait"===t.type),this.makeHighlightsSlider()},makeHighlightsSlider(){const t=this.highlightMovies[0].items.map(t=>`\n        <div class="item">\n          <a href="#">\n            <img src="${t.images[0].url}"\n            alt="${t.title}"\n            title="${t.title}">\n          </a>\n        </div>`).join("");$(".slider").html(t).removeClass("on-loading"),this.initHighlightsSlider()},initHighlightsSlider(){$(".slider").slick({centerMode:!0,centerPadding:"25%",dots:!0,autoplay:!1,autoplaySpeed:1e3,responsive:[{breakpoint:768,settings:{arrows:!1,dots:!1,centerMode:!1,centerPadding:0}}]})}};